mapper.select:
select FCODE,FNAME,FCHECKED,FSPECTYPE, FORIGINVALUE,FEXTENDSTR2,FDUTYPSNNAME, TO_CHAR(FCREATETIME,'yyyy-mm-dd') AS FCREATETIME, FSTATUSNAME,FDUTYDEPTNAME,FRESPONSEDEPTNAME,ADMINDEPT, FISFA,FASSETCONFIRM,FSOURCENAME,FRESPONSEPSNNAME, FISDEPT,FREMAINVALUE from OA_AS_CARD where 1=1
and FSTATUSNAME like '%'||#{FSTATUSNAME}||'%'
and FDUTYDEPTNAME like '%'||#{FDUTYDEPTNAME}||'%'
and FRESPONSEDEPTNAME like '%'||#{FRESPONSEDEPTNAME}||'%'
and ADMINDEPT like '%'||#{ADMINDEPT}||'%'
and FISFA like '%'||#{FISFA}||'%'
and FASSETCONFIRM like '%'||#{FASSETCONFIRM}||'%'
and FSOURCENAME like '%'||#{FSOURCENAME}||'%'
and FDUTYPSNNAME like '%'||#{FDUTYPSNNAME}||'%'
and FRESPONSEPSNNAME like '%'||#{FRESPONSEPSNNAME}||'%'
and FCHECKED like '%'||#{FCHECKED}||'%'
and FISDEPT like '%'||#{FISDEPT}||'%'
and to_char(FCREATETIME,'yyyy')=to_char(sysdate,'yyyy') and to_char(FCREATETIME,'mm')=to_char(sysdate,'mm') and to_char(FCREATETIME,'dd')=to_char(sysdate,'dd')
and to_char(FCREATETIME,'yyyy')=to_char(sysdate,'yyyy') and to_char(FCREATETIME,'mm')=to_char(sysdate,'mm') and to_char(FCREATETIME,'dd')=(to_char(sysdate,'dd')-1)
and to_char(FCREATETIME,'yyyy')=to_char(sysdate,'yyyy') and to_char(FCREATETIME,'iw')=to_char(sysdate,'iw')
and to_char(FCREATETIME,'yyyy')=to_char(sysdate,'yyyy') and to_char(FCREATETIME,'iw')=(to_char(sysdate,'iw')-1)
and to_char(FCREATETIME,'yyyy')=to_char(sysdate,'yyyy') and to_char(FCREATETIME,'mm')=to_char(sysdate,'mm')
and to_char(FCREATETIME,'yyyy')=to_char(sysdate,'yyyy') and to_char(FCREATETIME,'mm')=(to_char(sysdate,'mm')-1)
and to_char(FCREATETIME,'yyyy')=to_char(sysdate,'yyyy')
and to_char(FCREATETIME,'yyyy')=(to_char(sysdate,'yyyy')-1)
and FCREATETIME > to_date(#{pid1},'yyyy-mm-dd') and FCREATETIME < to_date(#{pid2},'yyyy-mm-dd')
and FORIGINVALUE = #{FORIGINVALUE}
and FORIGINVALUE > #{FORIGINVALUE}
and FORIGINVALUE < #{FORIGINVALUE}
and FORIGINVALUE >= #{FORIGINVALUE}
and FORIGINVALUE <= #{FORIGINVALUE}
and FORIGINVALUE <> #{FORIGINVALUE}
and FREMAINVALUE = #{FREMAINVALUE}
and FREMAINVALUE > #{FREMAINVALUE}
and FREMAINVALUE < #{FREMAINVALUE}
and FREMAINVALUE >= #{FREMAINVALUE}
and FREMAINVALUE <= #{FREMAINVALUE}
and FREMAINVALUE <> #{FREMAINVALUE}
and (FCODE like '%'||#{FUZZYSEARCH}||'%' or FNAME like '%'||#{FUZZYSEARCH}||'%' or FDUTYPSNNAME like '%'||#{FUZZYSEARCH}||'%' )
order by FCREATETIME desc
